#include<bits/stdc++.h>
using namespace std;
int p,l=0,x;
int a[1000010];
char c[330]={""};
int main()
{
	scanf("%d",&p);
	if(p>=1000)
	{
		for(int i=302;i>=1;i--) a[i]=c[302-i+1]-'0';
		l=302;
	}
	else memset(a,0,sizeof a);
	
	for(int i=1001;i<=p;i++)
	{
		x=0;
		for(int j=1;j<=l;j++) a[j]<<=1;
		for(int j=1;j<=l;j++) a[j]+=x,x=a[j]/10,a[j]%=10;
		if(x) a[++l]=x;
	}
	printf("%d\n",l);--a[1];
	for(int i=500;i>=1;i--) if(i%50==1&&i!=500) printf("%d\n",a[i]); else printf("%d",a[i]);
	return 0;
}
